YUI.add('autocomplete-highlighters',function(Y,NAME){var YArray=Y.Array,Highlight=Y.Highlight,Highlighters=Y.mix(Y.namespace('AutoCompleteHighlighters'),{charMatch:function(query,results,caseSensitive){var queryChars=YArray.unique((caseSensitive?query:query.toLowerCase()).split(''));return YArray.map(results,function(result){return Highlight.all(result.text,queryChars,{caseSensitive:caseSensitive});});},charMatchCase:function(query,results){return Highlighters.charMatch(query,results,true);},phraseMatch:function(query,results,caseSensitive){return YArray.map(results,function(result){return Highlight.all(result.text,[query],{caseSensitive:caseSensitive});});},phraseMatchCase:function(query,results){return Highlighters.phraseMatch(query,results,true);},startsWith:function(query,results,caseSensitive){return YArray.map(results,function(result){return Highlight.all(result.text,[query],{caseSensitive:caseSensitive,startsWith:true});});},startsWithCase:function(query,results){return Highlighters.startsWith(query,results,true);},subWordMatch:function(query,results,caseSensitive){var queryWords=Y.Text.WordBreak.getUniqueWords(query,{ignoreCase:!caseSensitive});return YArray.map(results,function(result){return Highlight.all(result.text,queryWords,{caseSensitive:caseSensitive});});},subWordMatchCase:function(query,results){return Highlighters.subWordMatch(query,results,true);},wordMatch:function(query,results,caseSensitive){return YArray.map(results,function(result){return Highlight.words(result.text,query,{caseSensitive:caseSensitive});});},wordMatchCase:function(query,results){return Highlighters.wordMatch(query,results,true);}});},'3.15.0',{"requires":["array-extras","highlight-base"]});