/**
 * Get the date for the current quarter
 *
 * @return array
 */
function get_quarter()
{
    $y = date('Y');
    $m = date('m');
    switch ($m) {
        case $m >= 1 && $m <= 3:
            $start = $y . '-01-01';
            $end = $y . '-03-31';
            break;
        case $m >= 4 && $m <= 6:
            $start = $y . '-04-01';
            $end = $y . '-06-30';
            break;
        case $m >= 7 && $m <= 9:
            $start = $y . '-07-01';
            $end = $y . '-09-30';
            break;
        case $m >= 10 && $m <= 12:
            $start = $y . '-10-01';
            $end = $y . '-12-31';
            break;
    }
    return array(
        'start' => $start,
        'end' => $end,
    );
}
